insert or replace into LocalizedText
    (Language,      Tag,                                                                Text)
values
    ("en_US",  "LOC_EVENT_BARB_TRIBE_UNITE_TITLE",                                 "Tribal Confederation"),
    ("en_US",  "LOC_EVENT_BARB_TRIBE_UNITE_DESCRIPTION",                           ""),
    ("en_US",  "LOC_EVENT_BARB_TRIBE_UNITE_EFFECT",                                "As the leader of tribal confederate, you have defeated the local tribes and forced the others to bend their knees. How will you decide the fate of these barbarians? Will you accept them as border guards, capture them as forced labor, or relocate them to nearby cities and make them civilized?"),
    ("en_US",  "LOC_EVENT_BARB_TRIBE_UNITE_CHOICE_A",                              "Border Guard"),
    ("en_US",  "LOC_EVENT_BARB_TRIBE_UNITE_CHOICE_B",                              "Forced Labor"),
    ("en_US",  "LOC_EVENT_BARB_TRIBE_UNITE_CHOICE_C",                              "Relocation"),
    ("en_US",  "LOC_EVENT_BARB_TRIBE_UNITE_GET_UNITS",                             "{1_Amount} nearby Barbarian combat units are converted to yours."),
    ("en_US",  "LOC_EVENT_BARB_TRIBE_UNITE_GET_BUILDER",                           "{1_Amount} nearby Barbarian combat units disappear, and you receive {2_Amount} Builders."),
    ("en_US",  "LOC_EVENT_BARB_TRIBE_UNITE_GET_POP",                               "{1_Amount} nearby Barbarian combat units disappear, {2_CITYNAME} receives {1_Amount} [ICON_Citizen] Population."),
    ("en_US",  "LOC_EVENT_BARB_TRIBE_UNITE_NO_STATE_LABOR",                        "[COLOR_RED]Can only be chosen after researching the State Workforce civic.[ENDCOLOR]"),
    ("en_US",  "LOC_EVENT_BARB_TRIBE_UNITE_NO_EARLY_EMPIRE",                       "[COLOR_RED]Can only be chosen after researching the Early Empire civic.[ENDCOLOR]"),   

    ("en_US",  "LOC_EVENT_COMMON_TRIBE_UNITY_QUEST1_TITLE",                        "Military Reforms"),
