    ("en_US",  "LOC_EVENT_COMMON_TRIBE_UNITY_QUEST1_EFFECT",                       "After conquering two nearby tribes, we have chosen veteran warriors from every tribe to form a standing army. Our military organization is stronger than ever, and they will serve as the blade to cut open the way of expansion!"),
    ("en_US",  "LOC_EVENT_COMMON_TRIBE_UNITY_QUEST1_CONTINUE",                     "For the horde!"),
    ("en_US",  "LOC_EVENT_COMMON_TRIBE_UNITY_QUEST1_UNLOCK",                       "Adds a military policy slot for the Tribal Confederate government."),

    ("en_US",  "LOC_EVENT_COMMON_TRIBE_UNITY_QUEST2_TITLE",                        "Economic Reforms"),
    ("en_US",  "LOC_EVENT_COMMON_TRIBE_UNITY_QUEST2_DESCRIPTION",                  ""),
    ("en_US",  "LOC_EVENT_COMMON_TRIBE_UNITY_QUEST2_EFFECT",                       "After assimilating five nearby villages, we have established the commerce system among different clans in our horde, a tight trade relationship among clans will be key to our unity."),
    ("en_US",  "LOC_EVENT_COMMON_TRIBE_UNITY_QUEST2_CONTINUE",                     "For the horde!"),
    ("en_US",  "LOC_EVENT_COMMON_TRIBE_UNITY_QUEST2_UNLOCK",                       "Adds an economic policy slot for the Tribal Confederate government."),

    ("en_US",  "LOC_EVENT_COMMON_CITYSTATE_QUEST1_TITLE",                          "Military Reforms"),
    ("en_US",  "LOC_EVENT_COMMON_CITYSTATE_QUEST1_DESCRIPTION",                    ""),
    ("en_US",  "LOC_EVENT_COMMON_CITYSTATE_QUEST1_EFFECT",                         "The military traditions of {1_CITYNAME} have brought us better understanding with establishing an army, now we can face war with ease."),
    ("en_US",  "LOC_EVENT_COMMON_CITYSTATE_QUEST1_CONTINUE",                       "The first step to military hegemony"),
    ("en_US",  "LOC_EVENT_COMMON_CITYSTATE_QUEST1_UNLOCK",                         "Adds a military policy slot for the City-State Suzerainty government."),

    ("en_US",  "LOC_EVENT_COMMON_CITYSTATE_QUEST2_TITLE",                          "Economic Reforms"),
