    ("en_US",  "LOC_EVENT_COMMON_CITYSTATE_QUEST2_EFFECT",                         "The economic traditions of {1_CITYNAME} have brought us better understanding with commere. Now, the prosperity of our trade network is unprecendented."),
    ("en_US",  "LOC_EVENT_COMMON_CITYSTATE_QUEST2_CONTINUE",                       "The first step to economic hegemony"),
    ("en_US",  "LOC_EVENT_COMMON_CITYSTATE_QUEST2_UNLOCK",                         "Adds an economic policy slot for the City-State Suzerainty government."),

    ("en_US",  "LOC_EVENT_GOODY_MURAL_TITLE",                                      "Cave Painting Arts"),
