    ("en_US",  "LOC_EVENT_GOODY_ANCIENT_FORTIFICATION_CHOICE_B",                   "Scavenge the Supplies"),
    ("en_US",  "LOC_EVENT_GOODY_ANCIENT_FORTIFICATION_GOOD_END",                   "30% to trigger the event“Successful Siege”"),
    ("en_US",  "LOC_EVENT_GOODY_ANCIENT_FORTIFICATION_BAD_END",                    "70% to trigger the event “Stalemate”, and your {1_UNITNAME} loses 25 HP"),
